    @SuppressWarnings("unchecked")
    
    public void setFields(UserDetails user)
    {
        txtEmpNo.setText(new Integer(user.getEmpNo()).toString());
        txtNICNo.setText(user.getNic());
        txtFirstName.setText(user.getFirstName());
        txtLastName.setText(user.getLastName());
        txtDesignation.setText(user.getDesignation());
        txtUserType.setText(user.getUserType());       
        txtAge.setText(new Integer(user.getAge()).toString());
        txtAddress.setText(user.getAddress());
        txtContactNo.setText(new Integer(user.getContactNo()).toString()); 
        if(new Integer(user.getAccountNo()).equals(0))
            txtAccountNo.setText("");
        else
            txtAccountNo.setText(new Integer(user.getAccountNo()).toString());
        chbMember.setSelected(user.isMember());
        txtuserName.setText(user.getUserName());
        
    
    
    }
    
